mapper.select:
SELECT SEQ, AUTHOR, TITLE, STATE, APPR_TURN, TYPE_SEQ, CONTENT,REGDATE 
	FROM DOCUMENT
		WHERE SEQ = #{seq}

mapper.update:
UPDATE DOCUMENT
	SET STATE=#{state}, APPR_TURN=#{appr_turn}, REGDATE=SYSDATE
	
	,CONTENT=#{content} ,TITLE=#{title}
	
		WHERE SEQ=#{seq}

mapper.select:
SELECT ID , CHK , TURN, NAME, DUTY, APPR_DATE, APPR_KIND, APPR_SIGN
	FROM APPROVAL 
		WHERE SEQ = #{seq}
		
			AND ID=#{id}

mapper.update:
UPDATE APPROVAL
	SET CHK =#{chk}, APPR_DATE=SYSDATE, APPR_SIGN=#{appr_sign}
		WHERE SEQ =#{seq} AND ID = #{id}

UPDATE DOCUMENT
	SET STATE =#{state}
	
		,APPR_TURN = (SELECT MAX(APPR_TURN) FROM DOCUMENT)+1
	
		WHERE SEQ =#{seq}
