mapper.update:
UPDATE FREE_COMMENTS
	SET CONTENT = '뭐시여?', REGDATE = SYSDATE 
		WHERE PARENT_SEQ = #{parent_seq}

mapper.select:
SELECT * FROM 
	(
	SELECT 
		ROW_NUMBER() OVER(ORDER BY SEQ ASC) AS ROW_NUM, 
			SEQ, ID, NAME, 
			CONTENT, REGDATE, PARENT_SEQ 
			FROM FREE_COMMENTS 
			WHERE PARENT_SEQ = #{parent_seq}
	)
			WHERE ROW_NUM BETWEEN #{start} AND #{last}

SELECT COUNT(*)
	FROM FREE_COMMENTS
		WHERE PARENT_SEQ = #{parent_seq}
