mapper.update:
UPDATE FREE_BBS
	SET TITLE=#{title}, CONTENT=#{content}
		WHERE SEQ=#{seq}

UPDATE FREE_BBS
	SET STATE = 1
		WHERE STATE = 0 AND SEQ = #{seq}

mapper.select:
 
	SELECT SEQ, ID, NAME, TITLE, NVL(CONTENT, ' ') CONTENT, STATE, REGDATE, VIEWS, ROOT, REPLY_SEQ, BBS_DEPTH
		FROM FREE_BBS
			WHERE SEQ = #{seq}

mapper.update:
UPDATE FREE_BBS 
	SET VIEWS = VIEWS+1
		WHERE SEQ = #{seq}

mapper.select:
SELECT NAME ,F_SIZE 
	FROM FILES 
		WHERE SEQ=#{seq}
