mapper.select:
SELECT SEQ, ID, NAME, TITLE, CONTENT, STATE, REGDATE, VIEWS, ROOT, REPLY_SEQ, BBS_DEPTH
	FROM NOTICE_BBS
		WHERE SEQ = #{seq}

mapper.update:
UPDATE NOTICE_BBS
	SET TITLE='#{title}', CONTENT='#{content}'
		WHERE SEQ=#{seq}

UPDATE NOTICE_BBS 
	SET STATE = #{state}
	
		SEQ IN 
		
				#{seq}
